</label>                    <input type="text" id="opddt_fimvigencia" name="opddt_fimvigencia" value="<?php echo isset($this->parametros->opddt_fimvigencia) ? $this->parametros->opddt_fimvigencia : ''; ?>" class="campo"/>                </div>                <div class="clear"></div>            </div>        </div>        <div class="bloco_acoes">            <button type="submit" id="botao_pesquisar">Pesquisar</button>            <button type="button" id="novo">Novo</button>        </div>        <div class='clear'></div>        <?php if (isset($this->resultado) && !empty($this->resultado)) { ?>            <div class="separador"></div>            <div class="bloco_titulo">Resultado da Pesquisa</div>            <div class="bloco_conteudo">                <div class="listagem">                    <table>                        <thead>                            <tr>                                <th class="maior">Operadora</th>                                <th class="centro">Início Vigência</th>                                <th class="centro">Fim Vigência</th>                                <th class="menor"></th>                            </tr>                        </thead>                        <tbody>                            <?php                            foreach ($this->resultado as $registro) {                                $class = $class == 'par' ? '' : 'par';                                echo "                                        <tr class=\"" . $class . "\">                                            <td>" . $registro->operadora . "</td>                                            <td class=\"centro\">" . $registro->data_inicio . "</td>                                            <td class=\"centro\">" . $registro->data_fim . "</td>                                            <td class='centro'>                                                <a href=\"cad_operadora_dia.php?acao=editar&id=" . $registro->id . "\"><img src=\"images/edit.png\" title=\"Editar\" class=\"icone\" /></a>                                                <a class='deletar' href=\"cad_operadora_dia.php?acao=deletar&id=" . $registro->id . "\"><img src='images/icon_error.png' title='Excluir' class=\"icone\" /></a>                                            </td>                                        </tr>                                        ";                            }                            ?>                        </tbody>                    </table>                </div>            </div>            <div class="bloco_acoes">                <?php $s =  count($this->resultado) > 1 ? 's' : ''; ?>                <?php echo "<p>" . count($this->resultado) . " registro". $s ." encontrado". $s .".</p>"; ?>            </div>        <?php } ?>    </form></div><div class="separador"></div><?php require 'lib/rodape.php'; ?>